sys_variable_value.value:
(function execute(inputs, outputs) {

    const GO = "go";
    const GOTIMER = "goTimer";
    const STOP = "stop";
    const TURN = "turn";
    const ISMOVING = "isMoving";

    if (inputs["action"] === GOTIMER) {
        outputs["rest_url"] = `/go-timer/${inputs["direction"]}/${inputs["speedPrimary"]}/${inputs["time"]}`
    }
    else if (inputs["action"] === GO) {
        outputs["rest_url"] = `/go/${inputs["direction"]}/${inputs["speedPrimary"]}`
    }
    else if (inputs["action"] === STOP) {
        outputs["rest_url"] = `/stop`
    }
    else if (inputs["action"] === TURN) {
        outputs["rest_url"] = `/turn/${inputs["angle"]}/${inputs["speedPrimary"]}/${inputs["speedSecondary"]}/${inputs["clockwise"]}`
    }
    else if (inputs["action"] === ISMOVING) {
        outputs["rest_url"] = `/is-moving`
    }
    else {
        outputs["rest_url"] = `/`
    }

})(inputs, outputs);

